import {PermissionRole} from './permission-role';
import {Organization} from './organization';

export class User {
  constructor(
    public name: string,
    public role?: string,
    public email?: string,
    public firstName?: string,
    public lastName?: string,
    public id?: number,
    public password?: string,
    public activeDirectory?: boolean,
    public status?: string,
    public disabledSince?: string,
    public loginCount?: number,
    public lastLoginDate?: string,
    public organization?: Organization,
    public organizationId?: number,
    public permissionRoles?: PermissionRole[]
  ) {}
  passwordExpireDate: string;
  accountExpireDate: string;

  sendSystemNotifications: boolean;
  sendPasswordToUser: boolean;
  generatePassword: boolean;
  sendNotificationPassword: boolean;
  accountLocked: boolean;
}
