import {Component, Input, OnInit} from '@angular/core';
import {User} from '../../models/user';
import {UserDevice} from '../../models/user-device';

@Component({
  selector: 'app-device',
  templateUrl: './device.control.component.html',
  styleUrls: ['./device.control.component.css']
})
export class DeviceControlComponent implements OnInit {

  @Input() user: User;

  @Input() dataSource: UserDevice[];

  constructor() {
  }

  ngOnInit(): void {
    this.dataSource.forEach(device => {
      device.lastDevice = new Date(device.lastDeviceLogin);
    });
  }


}
